import { Component, OnInit } from '@angular/core';

import { NavController, ModalController, ActionSheetController, LoadingController, ToastController, AlertController } from '@ionic/angular';
import { ApiService } from '../../services/api.service';
import { FormGroup , FormControl, Validators } from '@angular/forms';
import { PaisesSeleccionarPage } from '../../modals/paises-seleccionar/paises-seleccionar.page';
import { Storage } from '@ionic/storage';
import { TranslateService } from '@ngx-translate/core';
import * as moment from 'moment';
import { Camera, CameraOptions, PictureSourceType } from '@ionic-native/camera/ngx';

@Component({
  selector: 'app-perfil',
  templateUrl: './perfil.page.html',
  styleUrls: ['./perfil.page.scss'],
})
export class PerfilPage implements OnInit {
  form: FormGroup;
  imagen: string = '';
  constructor(private navController: NavController,
      public api: ApiService,
      private camera: Camera,
      private modalController: ModalController,
      private loadingController: LoadingController,
      private toastController: ToastController,
      public alertController: AlertController,
      public storage: Storage,
      public actionSheetController: ActionSheetController,
      private translateService: TranslateService) { }

  ngOnInit () {
    this.imagen = this.api.USER_ACCESS.imagen;

    this.form = new FormGroup ({
      nombres: new FormControl ({value: this.api.USER_ACCESS.nombres, disabled: this.api.USER_ACCESS.tipo !== 'agente'}, [Validators.required]),
      apellidos: new FormControl ({value: this.api.USER_ACCESS.apellidos, disabled: this.api.USER_ACCESS.tipo !== 'agente'}, Validators.required),
      celular: new FormControl (this.api.USER_ACCESS.telefono, [Validators.required]),
      codigo_pais_telf: new FormControl (this.api.USER_ACCESS.codigo_pais_telf, [Validators.required]),
      correo: new FormControl ({value: this.api.USER_ACCESS.correo, disabled: true}, [Validators.required]),
      pais: new FormControl (this.api.USER_ACCESS.pais, [Validators.required]),
      id_pais: new FormControl (this.api.USER_ACCESS.id_pais, [Validators.required])
    });

    if (this.api.USER_ACCESS.tipo === 'agente') {
      this.form.controls ['pais'].setValidators ([]);
      this.form.controls ['id_pais'].setValidators ([]);
      this.form.controls ['codigo_pais_telf'].setValidators ([]);
      this.form.updateValueAndValidity ();
    }
  }

  go_page (page: string) {
    this.navController.navigateRoot (page);
  }

  async seleccionar_pais () {
    const modal = await this.modalController.create({
      component: PaisesSeleccionarPage
    });

    modal.onWillDismiss ().then ((response: any) => {
      if (response.role === 'data') {
        this.form.controls ['pais'].setValue (response.data.nombre);
        this.form.controls ['id_pais'].setValue (response.data.id);
      }
    });

    return await modal.present ();
  }

  async submit () {
    const loading = await this.loadingController.create({
      message: this.api.get_translate ('Procesando...')
    });

    await loading.present ();

    let request: any = {}
    if (this.api.USER_ACCESS.tipo === 'agente') {
      request = {
        telefono: this.form.value.celular,
        idagente: this.api.USER_ACCESS.id,
        nombres: this.form.value.nombres,
        apellidos: this.form.value.apellidos
      };

      console.log (request);
      this.save_agente (request, loading);
    } else {
      request = {
        codigo_pais_telf: this.form.value.codigo_pais_telf,
        celular: this.form.value.celular,
        iduser: this.api.USER_ACCESS.id,
        id_pais_residencia: this.form.value.id_pais
      };

      console.log (request);
      this.api.actualizar_perfil (request).subscribe ((res: any) => {
        console.log (res);
        loading.dismiss ();
        this.presentToast (this.api.get_translate ('Perfil actualizado correctamente'), 'success');
      }, error => {
        console.log (error);
        loading.dismiss ();
      });
    }
  }

  async save_agente (request: any, loading: any) {
    this.api.actualizar_perfil_agente (request).subscribe ((res: any) => {
      console.log (res);
      loading.dismiss ();
      
      this.api.USER_ACCESS.nombres = request.nombres;
      this.api.USER_ACCESS.apellidos = request.apellidos;
      this.api.USER_ACCESS.celular = request.celular;
      this.api.USER_ACCESS.codigo_pais_telf = request.codigo_pais_telf;
      this.api.USER_ACCESS.imagen = this.imagen;
      
      this.presentToast (this.api.get_translate ('Perfil actualizado correctamente'), 'success');
    }, error => {
      console.log (error);
      loading.dismiss ();
    });
  }

  async presentToast (message: any, color: string) {
    const toast = await this.toastController.create ({
      message: message,
      color: color,
      duration: 2000,
      position: 'top'
    });

    toast.present ();
  }

  async salir () {
    const alert = await this.alertController.create ({
      header: this.api.get_translate ('Cerrar sesión'),
      message: this.api.get_translate ('¿Está seguro que desea cerrar sesión?'),
      buttons: [
        {
          text: this.api.get_translate ('Cancelar'),
          role: 'cancel'
        }, {
          text: this.api.get_translate ('Confirmar'),
          handler: async () => {
            const loading = await this.loadingController.create({
              message: this.api.get_translate ('Procesando...')
            });

            await loading.present ();

            this.api.logout ().subscribe (async (res: any) => {
              console.log (res);
              await loading.dismiss ();
              this.borrar_user_access ();
            }, async error => {
              console.log (error);
              await loading.dismiss ();
              this.borrar_user_access ();
            });
          }
        }
      ]
    });

    await alert.present();
  }

  borrar_user_access () {
    this.storage.remove ('USER_ACCESS').then (() => {
      this.navController.navigateRoot ('inicio-selector');
    });
  }

  async change_lang () {
    const actionSheet = await this.actionSheetController.create({
      header: 'Idioma',
      mode: 'md',
      buttons: [{
        text: 'Español',
        handler: () => {
          this.set_lang ('es');
        }
      }, {
        text: 'English',
        handler: () => {
          this.set_lang ('en');
        }
      }, {
        text: 'Português',
        handler: () => {
          this.set_lang ('pt');
        }
      }, {
        text: this.api.get_translate ('Cancelar'),
        role: 'cancel'
      }]
    });

    await actionSheet.present ();
  }

  set_lang (lang: string) {
    this.storage.set ('lang', lang);
    moment.locale (lang);
    this.translateService.use (lang);
    this.api.CURRENT_LANG = lang;
  }

  async save_image () {
    const actionSheet = await this.actionSheetController.create({
      header: 'Seleccione una fuente',
      buttons: [{
        text: 'Tomar una foto',
        icon: 'camera',
        handler: () => {
          this.takePicture (this.camera.PictureSourceType.CAMERA);
        }
      }, {
        text: 'Seleccionar de la galeria',
        icon: 'images',
        handler: () => {
          this.takePicture (this.camera.PictureSourceType.PHOTOLIBRARY);
        }
      }]
    });

    await actionSheet.present ();
  }

  async takePicture (sourceType: PictureSourceType) {
    // let options: CameraOptions = {
    //   quality: 95,
    //   sourceType: sourceType,
    //   saveToPhotoAlbum: false,
    //   correctOrientation: true,
    //   destinationType: this.camera.DestinationType.DATA_URL,
    //   encodingType: this.camera.EncodingType.JPEG,
    //   mediaType: this.camera.MediaType.PICTURE,
    // };

    // this.camera.getPicture (options).then(async (imageData) => {
      // this.imagen = 'data:image/jpeg;base64,' + imageData;
      this.imagen = 'data:image/jpeg;base64,';

      let request = {
        imagen: this.imagen,
        idagente: this.api.USER_ACCESS.id,
        telefono: this.form.value.celular,
        nombres: this.form.value.nombres,
        apellidos: this.form.value.apellidos
      };
      
      const loading = await this.loadingController.create({
        message: this.api.get_translate ('Procesando...')
      });
  
      await loading.present ();
  
      this.save_agente (request, loading);
    // }, (err) => {
    //   console.log ('Camera error', err);
    // });
  }

  async change_password () {
    const alert = await this.alertController.create ({
      header: 'Ingrese su nueva contraseña',
      inputs: [
        {
          name: 'password',
          type: 'text',
          placeholder: 'Contraseña Actual'
        },
        {
          name: 'password_new',
          type: 'text',
          placeholder: 'Contraseña Nueva'
        },
        {
          name: 'conf_password_new',
          type: 'text',
          placeholder: 'Confirmar Contraseña Nueva'
        }
      ],
      buttons: [
        {
          text: 'Cancelar',
          role: 'cancel'
        }, {
          text: 'Aceptar',
          handler: (data: any) => {
            if (data.password.trim () !== '' && data.password_new.trim () !== '' && data.conf_password_new.trim ()) {
              this.cambiar_pass (data.password, data.password_new, data.conf_password_new);
            } else {
              this.presentToast ('Ingrese todos los valores correctamente', 'danger');
            }
          }
        }
      ]
    });

    await alert.present();
  }

  async cambiar_pass (password: string, password_new: string, conf_password_new: string) {
    const loading = await this.loadingController.create({
      message: this.api.get_translate ('Procesando...')
    });

    await loading.present ();

    this.api.actualizar_password (
      this.api.USER_ACCESS.tipo, password, password_new, conf_password_new).subscribe ((res: any) => {
      console.log (res);
      loading.dismiss ();
      this.presentToast ('La contraseña se actualizo correctamente', 'success');
    }, error => {
      console.log (error);
      loading.dismiss ();
      this.presentToast ('La contraseña no coincide', 'danger');
    });
  }
}
